#!/bin/bash

apk add --update nodejs npm && cp .env.example .env && sed -i -r 's/^(DB_HOST=).*/\1mdb_mowatave/' ".env" && sed -i -r 's/^(DB_DATABASE=).*/\1${MYSQL_DATABASE}/' ".env" && sed -i -r 's/^(DB_PASSWORD=).*/\1${MYSQL_ROOT_PASSWORD}/' ".env" && sed -i -r 's/^(MAIL_HOST=).*/\1mh_mowatave/' ".env" && sed -i -r 's/^(MAIL_HOST=).*/\1mh_mowatave/' ".env" && composer install && php artisan key:generate && php artisan migrate --seed && npm install && npm run build 